import React, {useState} from 'react'
import './Dropdown.css'
import { IoIosArrowDown } from "react-icons/io";

const Dropdown = ({name, selected, toggleCollapse}) => {

  return (
    <div className='dropdown'>
        <div className='dropdown-btn'>
          {name}
          <span>
            <IoIosArrowDown onClick={() => toggleCollapse(name)} />
          </span>


        </div>
        <section className={selected === name ? 'show' : 'text-content'}>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quas nobis suscipit nostrum voluptas enim illo minus est nisi eos ut. Sit architecto est molestiae ad illo veritatis autem sequi dolores.
          </section>

    </div>
  )
}

export default Dropdown
