import React, {useState} from "react"
import Dropdown from "./Components/Dropdown/Dropdown"

const App = () => {

  const data = [{id:1, name:'data1'}, {id:2, name:'data2'}, {id:3, name:'data3'}, {id:4, name:'data4'}]
  const [selected, setSelected] = useState(null)
  const toggleCollapse = (name) => {
    if (selected === name){
      return setSelected(null)
    }
    setSelected(name)
  }
  return (
    // <Dropdown />
    data.map(el => <Dropdown key={el.id} name={el.name} selected={selected} toggleCollapse={toggleCollapse}/>)

  )
}

export default App
