import React, {useEffect, useState} from 'react'
import Dropdown from '../Dropdown/Dropdown'
import './Form.css'

const Form = () => {

  const [users, setUsers] = useState()
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [phone, setPhone] = useState('')
  const [gender, setGender] = useState('')
  const [selected, setSelected] = useState(false)
  const [editable, setEditable] = useState(false);

  const randomUser = () => {
    fetch("https://randomuser.me/api/")
    .then(response => response.json())
    .then(data => {
      // console.log (data)

      setUsers(data.results[0])
      setName(data.results[0].name.first+' '+data.results[0].name.last)
      setEmail(data.results[0].email)
      setPhone(data.results[0].phone)
      setGender(data.results[0].gender)
      setEditable(true)

    })
  }

  useEffect(() => {
    randomUser()
  }, [])

  const handleNameChange = (e) => {setName(e.target.value)}
  const handleEmailChange = (e) => {setEmail(e.target.value)}
  const handlePhoneChange = (e) => {setPhone(e.target.value)}
  const handleGenderChange = (word) => {setGender(word)
  toggleCollapse()}


  const toggleCollapse = () => {
    // if (selected === name){
    //   return setSelected(null)
    // }
    setSelected(!selected)
  }

  return (
  <div className='container'>
  <form>
    <div className="form">
          <label htmlFor="person-name" className="form-label">Name:</label>
          <input type="text" className="form-control" id="person-name" name="name" value={name} onChange={handleNameChange} />
        </div>
        <div className="form">
          <label htmlFor="person-email" className="form-label">Email:</label>
          <input type="email" className="form-control" id="person-email" name="email" value={email} onChange={handleEmailChange}/>
        </div>
        <div className="form">
          <label htmlFor="person-phone" className="form-label">Phone:</label>
          <input type="tel" className="form-control" id="person-phone" name="phone" value={phone} onChange={handlePhoneChange} />
        </div>
        <div className='form'>
          <label htmlFor="person-gender" className="form-label">Gender:</label>
          <Dropdown name={gender} toggleGender={handleGenderChange} selected={selected} toggleCollapse={toggleCollapse}/>
        </div>
        {!editable && <button type="button" className="btn btn-primary" onClick={handleEdit}>Edit</button>}
        {editable && <button type="submit" className="btn btn-success">Save</button>}

  </form>
  </div>
  )
}

export default Form
