import React, { useState, useEffect } from 'react';
// import { Dropdown, DropdownButton } from 'react-bootstrap';
import Dropdown from '../Dropdown/Dropdown';
import './Form.css'

function PersonForm() {
  const [person, setPerson] = useState(null);
  const [editable, setEditable] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [gender, setGender] = useState('');

  // Fetch data from the API
  useEffect(() => {
    fetch('https://randomuser.me/api/')
      .then(response => response.json())
      .then(data => {
        const personData = data.results[0];
        setName(`${personData.name.first} ${personData.name.last}`);
        setEmail(personData.email);
        setPhone(personData.phone);
        setGender('');
        setPerson(personData);
      });
  }, []);

  // Enable editing mode
  function handleEdit() {
    setEditable(true);
  }

  // Save changes and disable editing mode
  function handleSave(event) {
    event.preventDefault();
    setEditable(false);
    setPerson({
      ...person,
      name: { ...person.name, first: name.split(' ')[0], last: name.split(' ')[1] },
      email,
      phone,
      gender
    });
  }

  return (
    <div className="container">
      <h2>Person Information</h2>
      <form onSubmit={handleSave}>
        <div className="mb-3">
          <label htmlFor="person-name" className="form-label">Name</label>
          <input type="text" className="form-control" id="person-name" name="name" value={name} onChange={e => setName(e.target.value)} readOnly={!editable} />
        </div>
        <div className="mb-3">
          <label htmlFor="person-email" className="form-label">Email</label>
          <input type="email" className="form-control" id="person-email" name="email" value={email} onChange={e => setEmail(e.target.value)} readOnly={!editable} />
        </div>
        <div className="mb-3">
          <label htmlFor="person-phone" className="form-label">Phone</label>
          <input type="tel" className="form-control" id="person-phone" name="phone" value={phone} onChange={e => setPhone(e.target.value)} readOnly={!editable} />
        </div>
        <div className="mb-3">
          <label htmlFor="person-gender" className="form-label">Gender</label>
          {/* <DropdownButton id="gender-dropdown" title={gender ? gender : 'Select Gender'} disabled={!editable}>
            <Dropdown.Item onSelect={() => setGender('Male')}>Male</Dropdown.Item>
            <Dropdown.Item onSelect={() => setGender('Female')}>Female</Dropdown.Item>
            <Dropdown.Item onSelect={() => setGender('Other')}>Other</Dropdown.Item>
          </DropdownButton>  */}
        </div>
        {!editable && <button type="button" className="btn btn-primary" onClick={handleEdit}>Edit</button>}
        {editable && <button type="submit" className="btn btn-success">Save</button>}
      </form>
    </div>
  );
}

export default PersonForm;
