import React, {useEffect, useState} from 'react'

const Form = () => {

  const [users, setUsers] = useState()
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [phone, setPhone] = useState('')

  const randomUser = () => {
    fetch("https://randomuser.me/api/")
    .then(response => response.json())
    .then(data => {
      // console.log (data)

      setUsers(data.results[0])
      setName(data.results[0].name.first+' '+data.results[0].name.last)
      setEmail(data.results[0].email)
      setPhone(data.results[0].phone)



    })
  }

  useEffect(() => {
    randomUser()
  }, [])

  return (
  //   <div>
  //   {users.map(user => (
  //     <div key={user.login.uuid}>
  //       <img src={user.picture.medium} alt={user.name.first}/>
  //       <p>Name: {user.name.first} {user.name.last}</p>
  //       <p>Email: {user.email}</p>
  //       <p>Phone: {user.phone}</p>
  // </div>
  //   ))}
  // </div>

  <form>
    <div className="mb-3">
          <label htmlFor="person-name" className="form-label">Name</label>
          <input type="text" className="form-control" id="person-name" name="name" value={name} onChange={setName} />
        </div>
        <div className="mb-3">
          <label htmlFor="person-email" className="form-label">Email</label>
          <input type="email" className="form-control" id="person-email" name="email" value={email} onChange={setEmail}/>
        </div>
        <div className="mb-3">
          <label htmlFor="person-phone" className="form-label">Phone</label>
          <input type="tel" className="form-control" id="person-phone" name="phone" value={phone} onChange={setPhone} />
        </div>

  </form>
  )
}

export default Form
