import React, {useEffect, useState} from "react"
// import Dropdown from "./Components/Dropdown/Dropdown"

const App = () => {

  const [users, setUsers] = useState([])

  const fetchUserData = () => {
    fetch("https://randomuser.me/api")
    .then(response => {
      return response.json()
    })
    .then(data => {
      setUsers(data)
    })
  }

  useEffect(() => {
    fetchUserData()
  }, [])

  return (
    // <Dropdown />
    <div>
      {users.length > 0 && (
        <ul>
          {users.map(user => (
            <li key={user.id}>{user.name}</li>
          ))}
        </ul>
      )}



    </div>
  )
}

export default App
